const cNonceBuf = new ArrayBuffer(10);
let c_Nonce = new Uint8Array(cNonceBuf);


const NodeRSA = require('../src/NodeRSA');
const key = new NodeRSA({b: 512});

c_Nonce[0] = 54;
console.log(JSON.stringify(c_Nonce))

const encrypted = key.encrypt(JSON.stringify(c_Nonce), 'base64');
console.log('encrypted: ', encrypted);

const decrypted = key.decrypt(encrypted, "utf8");
console.log(decrypted);